import React, {useEffect, useRef, useState } from 'react';
import MainPath from '../ContentSecComponents/MainPath';
import SectionLessons from '../ContentSecComponents/SectionLessons';

function Content() {
    const [section, setSection] = useState<Number>(-1); // user chooses section in MainPath to go through the lessons. This useState loads the approriate sections lessons
    const [sectionsData, setSectionsData] = useState<object[]>([]);
    const mainPathRef = useRef<HTMLDivElement>(null);
    useEffect(()=>{
        if(section!=-1){
            setTimeout(() => {
                if(mainPathRef.current!=null){ // check for typescript
                    mainPathRef.current.style.display='none';
                }
            }, 2000);
        }
        fetch('http://localhost:8080/sectionsData')
            .then(res=>{
            res.json()
                .then(finalData=>{
                setSectionData(finalData);
            })
            })
    },[section])

    return (
        <main>
            <nav className='main-left-nav'>

            </nav>
            <MainPath sectionLessons={{section, setSection}} refs={{mainPathRef}}/>
            {section!=-1 ? <SectionLessons section={section}/> : null}
            <div className='main-right'>

            </div>
        </main>
    );
}

export default Content;
