import React, { useEffect, useRef } from 'react';

interface props{
    lessonData: any
}
interface singleBadAnswer{
    questionTitle: string,
    questionObject: {
        correctAnswer: string[],
        possibleAnswers: string[],
        questionFormat: string,
        title: string
    },
    answeredWord: string[]
}

function LessonOverview(props: props) {


    useEffect(()=>{
        console.log(props.lessonData);
    },[])

    return (
        <div className='lesson-overview'>
            <p className='lesson-overview-title'>Here are your results:</p>
            <div className='lesson-overview-results'>
                <p className='percentage'>Percentage of answers gotten right: {props.lessonData.goodAnswersArr.length / (props.lessonData.goodAnswersArr.length + props.lessonData.badAnswersArr.length) * 100}%</p>
                <p>The questions you have got wrong:
                </p>
                {props.lessonData.badAnswersArr.map((badAnswer: singleBadAnswer, index: number)=>{
                    return(
                        <div className='single-badAnswer' key={parseFloat(badAnswer.questionTitle)}>
                            <p className='badAnswer-title'>{badAnswer.questionTitle}</p>
                            <div className='answers'>
                                <p className='wys'>What you selected:</p>
                                <div className='single-badAnswer-userAnsweredWords'>
                                    {badAnswer.answeredWord.map((singleAnswer: string, i: number)=>{
                                        return(
                                            <p key={singleAnswer+i}>{singleAnswer}</p>
                                        )
                                    })}
                                </div>
                                <p className='tca'>The correct answers:</p>
                                <div className='single-badAnswer-correctWords'>
                                    {badAnswer.questionObject.correctAnswer.map((singleAnswer: string, i: number)=>{
                                        return(
                                            <p key={singleAnswer+i}>{singleAnswer}</p>
                                        )
                                    })}
                                </div>
                            </div>
                        </div>
                    )
                })}
            </div>
        </div>
    );
}

export default LessonOverview;
