import React, { useState } from 'react';
import MainPath from '../ContentSecComponents/MainPath';
import SectionLessons from '../ContentSecComponents/SectionLessons';

function Content() {
    const [startSecLessons, setStartSecLessons] = useState<Number>(-1); // user chooses section in MainPath to go through the lessons. This useState loads the approriate sections lessons

    return (
        <main>
            <nav className='main-left-nav'>

            </nav>
            <MainPath props={{startSecLessons, setStartSecLessons}}/>
            {startSecLessons!=-1 ? <SectionLessons section={startSecLessons}/> : null}
            <div className='main-right'>

            </div>
        </main>
    );
}

export default Content;
