        }
        // console.log(arrayIndividualLessons[questionCounterRef.current])
        if(arrayIndividualLessons[1].title){ // it is not the title array element
            console.log(arrayIndividualLessons[1].title)
        }

        return(
            <div>

            </div>
        )
    }

    return (
        <div className='lessons-div-fadeInAnim' ref={mainCompRef}>
            <div className='single-lesson'>
                {loadQuestions(arrayIndividualLessons)}
            </div>
        </div>
    );
}

export default SectionLessons;
