import React, { useContext, useEffect, useState } from 'react';
import UIDContext from 'src/UIDContext';

interface singleSectionsScore{
    sectionScore: number,
    badAnswersQnty: number,
    goodAnswersQnty: number
}

function MainRightScore(){
    const { UID, setUID } = useContext(UIDContext);

    const [finalScorePrcnt, setFinalScorePrcnt] = useState<string>('');

    useEffect(()=>{
        let xhr = new XMLHttpRequest();
        xhr.open("POST", 'http://localhost:8080/getUsersScore', true);
        xhr.setRequestHeader('Content-Type', 'application/json');
        xhr.send(JSON.stringify({
            UID: UID
        }));

        xhr.onload = ()=>{
            const parsedResponse = JSON.parse(xhr.responseText);
            console.log(parsedResponse);
            let tempHolder = parsedResponse.reduce((previousValue: number, singleSectionsScore_current: singleSectionsScore)=>{
                singleSectionsScore_current.sectionScore + previousValue,0;
            })
            console.log(tempHolder)
        }
    },[])

    return (
        <div className='main-right-score'>
            <img src={require('../../Photos/world.png')} alt='world icon'></img>
            <p className='main-right-score-title'>Unlock your score</p>
            <p>Finish at least 1 lesson to see your score!</p>
        </div>
    );
}

export default MainRightScore;
