import React, { useEffect, useRef } from 'react';

interface props{
    lessonData: any
}
interface singleBadAnswer{
    questionTitle: string,
    questionObject: {
        correctAnswer: string[],
        possibleAnswers: string[],
        questionFormat: string,
        title: string
    },
    answeredWord: string[]
}

function LessonOverview(props: props) {


    useEffect(()=>{
        console.log(props.lessonData);
    },[])

    return (
        <div className='lesson-overview'>
            <p>Here are your results:</p>
            <div className='lesson-overview-results'>
                <p>Percentage of answers gotten right: {props.lessonData.goodAnswersArr.length / (props.lessonData.goodAnswersArr.length + props.lessonData.badAnswersArr.length) * 100}%</p>
                <p>Your mistakes:</p>
                {props.lessonData.badAnswersArr.map((badAnswer: singleBadAnswer, index: number)=>{
                    return(
                        <div className='single-badAnswer'>
                            <p className='badAnswer-title'>{badAnswer.questionTitle}</p>
                            <div className='answers'>
                            <p>What you selected:</p>
                            <div className='single-badAnswer-userAnsweredWords'>
                                {badAnswer.answeredWord.map((singleAnswer: string, i: number)=>{
                                    return(
                                        <p>{singleAnswer}</p>
                                    )
                                })}
                            </div>
                            <p>The correct answers:</p>
                            <div className='single-badAnswer-userAnsweredWords'>
                                {badAnswer.questionObject.correctAnswer.map((singleAnswer: string, i: number)=>{
                                    return(
                                        <p>{singleAnswer}</p>
                                    )
                                })}
                            </div>
                            </div>
                        </div>
                    )
                })}
            </div>
        </div>
    );
}

export default LessonOverview;
