import React, { useEffect, useState } from 'react';


interface sectionDataI extends Array<string>{
    secNum: number,
    secDesc: string,
    individualLessons: Object
}

function MainPath() {

    const [sectionData, setSectionData] = useState<sectionDataI>([{secNum: 1, secDesc: 'axo', individualLessons: {}}]);

    useEffect(()=>{
        fetch('http://localhost:8080/sectionsData')
          .then(res=>{
            res.json()
              .then(finalData=>{
                setSectionData(finalData);
            })
          })
    },[])

    useEffect(()=>{
        console.log(sectionData)
    },[sectionData])

    return (
        <div className='main-path'>
            <div className='section-1'>
                <div className='section-name-div'>
                    <p>Section 1: The start - simple words</p>
                    <button>Start</button>
                </div>
                <div className='sections-lesson'>
                    <button><p>Lesson 1: Sort the verbs</p></button>
                    <button><p>Lesson 2: Find the correct verb</p></button>
                    <button><p>Lesson 3: Select the correct verbs</p></button>
                    <button><p>Lesson 4: Find the correct verb</p></button>
                    <button><p>Lesson 5: Select the corect verb</p></button>
                </div>
            </div>
            {sectionData.map(singleSection=>{
                console.log(singleSection)
                return(<div className={`section-${singleSection.secNum}`}></div>)
            })}
        </div>
    );
}

export default MainPath;
